import React, { Component } from 'react';
import './App.css';
// First way to import
import { ClipLoader } from 'react-spinners';

class App extends Component {

  constructor(){
    super();
    this.state = {
      username: '',
      id: '',
      url: '',
      avatar_url: '',
      loading: true
    }
  }

  getUser(username){
    return fetch(`https://api.github.com/users/${username}`)
    .then(response => response.json())
    .then(response => {
      return response;
    })
  }

  async handleSubmit(e){
    e.preventDefault();
    let user = await this.getUser(this.refs.username.value);
    this.setState({
      loading: false,
      username: user.login,
      id: user.id,
      url: user.url,
      avatar_url: user.avatar_url
    });
  }

  render() {
    let user;
    let content;
    if(this.state.username){
      if (this.state.loading) {
     content = <div>Loading...</div>;
     } else{
     user =
     <div>
       <img alt="user-dp" src={this.state.avatar_url} width={"220px"} />
       <p><strong>Username: </strong>{this.state.username}</p>
       <p><strong>User id: </strong>{this.state.id}</p>
       <p><strong>Profile URL: </strong>
         <a href={"https://github.com/" + this.state.username}>{"https://github.com/" + this.state.username}</a>
       </p>
     </div>

    }
    }
    return (

      <div className="app sweet-loading">
        <ClipLoader
          className={"override"}
          sizeUnit={"px"}
          size={150}
          color={'#123abc'}
          loading={this.state.loading}
        />
        <header className={"app-header"}>
          <h1>Github Finder</h1>
        </header>
        <form onSubmit={e => this.handleSubmit(e)}>
          <label className={"label-finder"}>Search github user: </label>
          <input className={"input-finder"} ref='username' type="text" placeholder='Enter Username' />
        </form>
        <div className={"user-data"}>
          {user}
        </div>
      </div>
    );
  }
}

export default App;
